body.script:
// Bengali Date
const bengaliMonths = ['জানুয়ারি', 'ফেব্রুয়ারি', 'মার্চ', 'এপ্রিল', 'মে', 'জুন', 'জুলাই', 'আগস্ট', 'সেপ্টেম্বর', 'অক্টোবর', 'নভেম্বর', 'ডিসেম্বর'];
const bengaliDays = ['রবিবার', 'সোমবার', 'মঙ্গলবার', 'বুধবার', 'বৃহস্পতিবার', 'শুক্রবার', 'শনিবার'];
const bengaliNumerals = ['০', '১', '২', '৩', '৪', '৫', '৬', '৭', '৮', '৯'];

function toBengaliNumber(num) {
    return num.toString().split('').map(digit => bengaliNumerals[parseInt(digit)]).join('');
}

function updateDate() {
    const now = new Date();
    const day = bengaliDays[now.getDay()];
    const date = toBengaliNumber(now.getDate());
    const month = bengaliMonths[now.getMonth()];
    const year = toBengaliNumber(now.getFullYear());

    document.getElementById('bengali-date').textContent = `${day}, ${date} ${month} ${year}`;
}

updateDate();
